import { useState, useEffect } from 'react';
import MyComponent from './selects_flota';
import Select from 'react-select';
import Swal from 'sweetalert2';
import axios from 'axios';
import SelectOperador from './select_operador';
import ManiobraContenedores from './maniobra_contenedores';
import { Avatar, AvatarGroup, AvatarIcon } from "@nextui-org/avatar";
import { User } from "@nextui-org/react";
import ModalCorreos from './correos';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Dialog from '@mui/material/Dialog';

const fieldValidations = {
    terminal: { required: true, message: 'El campo Terminal es requerido' },
    tipo_maniobra: { required: true, message: 'El campo Tipo de Maniobra es requerido' },
    operador_id: { required: true, message: 'El campo Operador es requerido' },
    vehicle_id: { required: true, message: 'El campo Vehiculo es requerido' },
    inicio_programado: { required: true, message: 'El campo Inicio Programado es requerido' }
};

const Formulariomaniobra = ({ show, handleClose, id_maniobra, id_cp }) => {
    const [formDisabled, setFormDisabled] = useState(true);
    const [htmlContent, setHtmlContent] = useState('');

    const [isPrimaryModalOpen, setIsPrimaryModalOpen] = useState(false);
    const openPrimaryModal = () => setIsPrimaryModalOpen(true);
    const closePrimaryModal = () => setIsPrimaryModalOpen(false);

    useEffect(() => {
        const controller = new AbortController();
        if (show) {
            axios
                .post("/phicargo/modulo_maniobras/maniobra/historial.php?id_maniobra=" + id_maniobra, { signal: controller.signal })
                .then((response) => {
                    setHtmlContent(response.data);
                })
                .catch((error) => {
                    if (error.name !== 'AbortError') {
                        console.error('Error al cargar el historial:', error);
                    }
                });
        }

        return () => {
            controller.abort();
        };
    }, [show]);

    const [buttonsVisibility, setButtonsVisibility] = useState({
        registrar: false,
        cancelar: false,
        editar_maniobra: false,
        iniciar: false,
        finalizar: false,
        enviar_estatus: false,
        reactivar: false
    });

    const toggleButtonsVisibility = (variable) => {
        const newVisibility = {};

        if (variable === 'borrador') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = true;
            newVisibility.cancelar = true;
            newVisibility.iniciar = true;
            newVisibility.finalizar = false;
            newVisibility.correos = true;
        } else if (variable === 'cancelada') {
            newVisibility.registrar = false;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
            newVisibility.correos = false;
        } else if (variable === 'registrar') {
            newVisibility.registrar = true;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
            newVisibility.correos = false;
        } else if (variable === 'editar') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = false;
            newVisibility.guardar_maniobra = true;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
            newVisibility.correos = false;
        } else if (variable === 'finalizada') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = false;
            newVisibility.guardar_maniobra = false;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
            newVisibility.reactivar = true;
            newVisibility.correos = false;
        } else if (variable === 'activa') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = false;
            newVisibility.guardar_maniobra = false;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = true;
            newVisibility.reactivar = false;
            newVisibility.correos = false;
        }

        setButtonsVisibility(newVisibility);
    };

    const toggleForm = () => {
        setFormDisabled(false);
        toggleButtonsVisibility('editar');
    };

    const options_tipo_maniobra = [
        { value: 'ingreso', label: 'Ingreso' },
        { value: 'retiro', label: 'Retiro' }
    ];

    const terminales_select = [
        { value: 'CIMA', label: 'CIMA' },
        { value: 'TIMSA', label: 'TIMSA' },
        { value: 'SSA', label: 'SSA' },
        { value: 'ICAVE', label: 'ICAVE' },
        { value: 'ICAVE ZAL', label: 'ICAVE ZAL' },
        { value: 'CCIS', label: 'CCIS' },
        { value: 'ISL', label: 'ISL' },
        { value: 'CONTENEDORES DEL GOLFO', label: 'CONTENEDORES DEL GOLFO' },
        { value: 'SANTA FE', label: 'SANTA FE' },
        { value: 'ICAVE CCI', label: 'ICAVE CCI' },
        { value: 'CONTAINER PARK', label: 'CONTAINER PARK' },
        { value: 'CICE SAN JULIAN', label: 'CICE SAN JULIAN' },
        { value: 'TERRAPORTS', label: 'TERRAPORTS' },
        { value: 'ICAVE T2', label: 'ICAVE T2' },
        { value: 'CICE', label: 'CICE' },
        { value: 'RICSA', label: 'RICSA' },
        { value: 'GOLMEX AGM', label: 'GOLMEX AGM' },
        { value: 'GOLMEX PUERTO', label: 'GOLMEX PUERTO' },
        { value: 'CIF', label: 'CIF' },
        { value: 'GARCA', label: 'GARCA' },
        { value: 'SEABORD', label: 'SEABORD' },
        { value: 'CONTECON', label: 'CONTECON' },
        { value: 'OCUPA', label: 'OCUPA' },
        { value: 'IMPALA', label: 'IMPALA' },
        { value: 'HAZESA', label: 'HAZESA' },
        { value: 'ALSECONT', label: 'ALSECONT' },
        { value: 'TEP', label: 'TEP' },
        { value: 'SLTC', label: 'SLTC' },
        { value: 'FRIGMANT', label: 'FRIGMANT' },
        { value: 'PATIO REGULADOR/TERMINAL SSA', label: 'PATIO REGULADOR/TERMINAL SSA' },
        { value: 'PATIO REGULADOR/PATIO EXTERNO 2 SSA', label: 'PATIO REGULADOR/PATIO EXTERNO 2 SSA' },
        { value: 'PATIO REGULADOR/RECINTO FISCALIZADO SSA', label: 'PATIO REGULADOR/RECINTO FISCALIZADO SSA' },
        { value: 'RECINTO FISCALIZADO SSA', label: 'RECINTO FISCALIZADO SSA (MZN)' }
    ];

    const [formData, setFormData] = useState({
        id_maniobra: id_maniobra,
        id_cp: id_cp,
        terminal: '',
        tipo_maniobra: '',
        operador_id: '',
        vehicle_id: '',
        trailer1_id: '',
        trailer2_id: '',
        dolly_id: '',
        inicio_programado: '',
        usuario_activo: '',
        usuario_finalizo: '',
        estado_maniobra: ''
    });

    useEffect(() => {
        if (id_maniobra) {
            setFormDisabled(true);
            axios.get(`/phicargo/modulo_maniobras/maniobra/get_maniobra.php?id_maniobra=${id_maniobra}`)
                .then((response) => {
                    const data = response.data[0];
                    console.log(data);
                    setFormData({
                        id_maniobra: data.id_maniobra || '',
                        id_cp: data.id_cp || '',
                        terminal: data.terminal || '',
                        tipo_maniobra: data.tipo_maniobra || '',
                        operador_id: data.operador_id || '',
                        vehicle_id: data.vehicle_id || '',
                        trailer1_id: data.trailer1_id || '',
                        trailer2_id: data.trailer2_id || '',
                        dolly_id: data.dolly_id || '',
                        motogenerador_1: data.motogenerador_1 || '',
                        motogenerador_2: data.motogenerador_2 || '',
                        inicio_programado: data.inicio_programado || '',
                        usuario_activo: data.usuarioactivacion || '',
                        usuario_finalizo: data.usuariofinalizacion || '',
                        estado_maniobra: data.estado_maniobra || ''
                    });
                    toggleButtonsVisibility(data.estado_maniobra);
                })
                .catch((error) => {
                    console.error('Error al obtener datos de maniobra:', error);
                    Swal.fire(
                        'Error',
                        'No se pudo cargar los datos de la maniobra.',
                        'error'
                    );
                });
        } else {
            setFormDisabled(false);
            setFormData({
                id_cp: id_cp,
                terminal: '',
                tipo_maniobra: '',
                operador_id: '',
                vehicle_id: '',
                trailer1_id: '',
                trailer2_id: '',
                dolly_id: '',
                motogenerador_1: '',
                motogenerador_2: '',
                inicio_programado: '',
                estado_maniobra: ''
            });
            toggleButtonsVisibility('registrar');
        }
    }, [id_maniobra]);

    const [errors, setErrors] = useState({});
    const validateFields = () => {
        let isValid = true;
        let newErrors = {};

        Object.keys(fieldValidations).forEach(field => {
            const validation = fieldValidations[field];
            if (validation.required && !formData[field]) {
                newErrors[field] = validation.message;
                isValid = false;
            }
        });

        return { isValid, newErrors };
    };

    const validar_form = (e) => {
        e.preventDefault();
        const { isValid, newErrors } = validateFields();
        setErrors(newErrors);

        if (isValid) {
            console.log('Datos del formulario:', formData);
            registrar_maniobra();
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSelectChange = (selectedOption, { name }) => {
        setFormData((prevData) => ({
            ...prevData,
            [name]: selectedOption ? selectedOption.value : '',
        }));
        console.log('Datos del formulario:', formData);
    };

    const registrar_maniobra = () => {
        axios.post('/phicargo/modulo_maniobras/maniobra/registrar_maniobra.php', formData)
            .then((response) => {
                const data = response.data;
                if (data.success) {
                    Swal.fire(
                        '¡Éxito!',
                        'El registro ha sido exitoso.',
                        'success'
                    );
                    handleClose();
                } else {
                    console.error('Respuesta inesperada del servidor:', data.error);
                    Swal.fire(
                        'Error',
                        'El registro falló debido a una respuesta inesperada: ' + data,
                        'error'
                    );
                }
            })
            .catch((error) => {
                console.error('Error en la solicitud:', error);
                if (error.response) {
                    console.error('Respuesta de error del servidor:', error.response.data);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al registrar la maniobra. Código de error: ' + error.response.status,
                        'error'
                    );
                } else if (error.request) {
                    console.error('No se recibió respuesta del servidor:', error.request);
                    Swal.fire(
                        'Error',
                        'No se recibió respuesta del servidor.',
                        'error'
                    );
                } else {
                    console.error('Error al configurar la solicitud:', error.message);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al configurar la solicitud.',
                        'error'
                    );
                }
            });
    };

    const actualizar_maniobra = () => {
        console.log(id_maniobra);
        axios.post('/phicargo/modulo_maniobras/maniobra/actualizar_maniobra.php', formData)
            .then((response) => {
                const data = response.data;
                if (data.success) {
                    Swal.fire(
                        '¡Éxito!',
                        'El registro ha sido exitoso.',
                        'success'
                    );
                    handleClose();
                    setFormDisabled(true);
                    toggleButtonsVisibility('borrador');
                } else {
                    console.error('Respuesta inesperada del servidor:', data.error);
                    Swal.fire(
                        'Error',
                        'El registro falló debido a una respuesta inesperada: ' + data,
                        'error'
                    );
                }
            })
            .catch((error) => {
                console.error('Error en la solicitud:', error);
                if (error.response) {
                    console.error('Respuesta de error del servidor:', error.response.data);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al registrar la maniobra. Código de error: ' + error.response.status,
                        'error'
                    );
                } else if (error.request) {
                    console.error('No se recibió respuesta del servidor:', error.request);
                    Swal.fire(
                        'Error',
                        'No se recibió respuesta del servidor.',
                        'error'
                    );
                } else {
                    console.error('Error al configurar la solicitud:', error.message);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al configurar la solicitud.',
                        'error'
                    );
                }
            });
    };

    const reactivar_maniobra = () => {
        console.log(id_maniobra);
        axios.post('/phicargo/modulo_maniobras/maniobra/reactivar_maniobra.php', formData)
            .then((response) => {
                if (response.data === 1) {
                    Swal.fire(
                        '¡Éxito!',
                        'El registro ha sido exitoso.',
                        'success'
                    );
                    handleClose();
                    toggleButtonsVisibility('borrador');
                } else {
                    console.error('Respuesta inesperada del servidor:', response.data);
                    Swal.fire(
                        'Error',
                        'El registro falló debido a una respuesta inesperada: ' + response.data,
                        'error'
                    );
                }
            })
            .catch((error) => {
                console.error('Error en la solicitud:', error);
                if (error.response) {
                    console.error('Respuesta de error del servidor:', error.response.data);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al registrar la maniobra. Código de error: ' + error.response.status,
                        'error'
                    );
                } else if (error.request) {
                    console.error('No se recibió respuesta del servidor:', error.request);
                    Swal.fire(
                        'Error',
                        'No se recibió respuesta del servidor.',
                        'error'
                    );
                } else {
                    console.error('Error al configurar la solicitud:', error.message);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al configurar la solicitud.',
                        'error'
                    );
                }
            });
    };

    const activar_maniobra = () => {
        Swal.fire({
            title: 'Activar',
            text: "¿Deseas realmente activar esta maniobra?",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, activar',
            cancelButtonText: 'Cancelar'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.post('/phicargo/modulo_maniobras/maniobra/activar_maniobra.php?id_maniobra=' + id_maniobra)
                    .then((response) => {
                        const data = response.data;
                        if (data.success) {
                            Swal.fire(
                                '¡Activada!',
                                'La maniobra ha sido activada',
                                'success'
                            );
                            handleClose();
                        } else {
                            Swal.fire(
                                'Error',
                                response.data,
                                'error'
                            );
                        }
                    })
                    .catch((error) => {
                        Swal.fire(
                            'Error',
                            'Hubo un problema al activar el registro.',
                            'error'
                        );
                    });
            }
        });
    };

    const finalizar_maniobra = () => {
        Swal.fire({
            title: 'Finalizar maniobra',
            text: "¿Realmente deseas finalizar esta maniobra?",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, finalizar',
            cancelButtonText: 'Cancelar'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.post('/phicargo/modulo_maniobras/maniobra/finalizar_maniobra.php?id_maniobra=' + id_maniobra)
                    .then((response) => {
                        const data = response.data;
                        if (data.success) {
                            Swal.fire(
                                '¡Finalizada!',
                                'Maniobra finalizada.',
                                'success'
                            );
                            handleClose();
                        } else {
                            console.error(data.error);
                            Swal.fire(
                                'Error',
                                data.error,
                                'error'
                            );
                        }
                    })
                    .catch((error) => {
                        Swal.fire(
                            'Error',
                            'Hubo un problema al eliminar el registro.',
                            'error'
                        );
                    });
            }
        });
    };

    const cancelar_maniobra = () => {
        Swal.fire({
            title: '¿Estás seguro?',
            text: "¿Deseas realmente cancelar este registro?",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, cancelar',
            cancelButtonText: 'Cancelar'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.post('/phicargo/modulo_maniobras/maniobra/cancelar_maniobra.php?id_maniobra=' + id_maniobra)
                    .then((response) => {
                        if (response.data === 1) {
                            Swal.fire(
                                '¡Cancelado!',
                                'El registro ha sido cancelado.',
                                'success'
                            );
                            handleClose();
                        } else {
                            console.error(response.data);
                        }
                    })
                    .catch((error) => {
                        Swal.fire(
                            'Error',
                            'Hubo un problema al eliminar el registro.',
                            'error'
                        );
                    });
            }
        });
    };

    return (
        <>
            <ModalCorreos
                show={isPrimaryModalOpen}
                handleClose={closePrimaryModal}
                id_cp={id_cp}
            />

            <Dialog open={show} onClose={handleClose} fullscreen>

                <AppBar sx={{ position: 'relative' }}>
                    <Toolbar>
                        <IconButton
                            edge="start"
                            color="inherit"
                            onClick={handleClose}
                            aria-label="close"
                        >
                            <CloseIcon />
                        </IconButton>
                        <Typography sx={{ ml: 2, flex: 1 }} variant="h6" component="div">
                            Sound
                        </Typography>
                        <Button autoFocus color="inherit" onClick={handleClose}>
                            save
                        </Button>
                    </Toolbar>
                </AppBar>

                <div class="page-header">
                    <div class="row align-items-center">
                        <div class="col-sm mb-2 mb-sm-0">
                            <h2 class="page-header-title">M-{id_maniobra}</h2>
                        </div>
                    </div>

                    <div className="flex flex-wrap gap-2 items-center">
                        {buttonsVisibility.registrar && <Button variant="contained" onClick={validar_form} size='sm'>Registrar</Button>}
                        {buttonsVisibility.cancelar && <Button variant="contained" onClick={cancelar_maniobra} size='sm'>Cancelar</Button>}
                        {buttonsVisibility.guardar_maniobra && <Button variant="contained" size='sm' onClick={actualizar_maniobra}>Guardar cambios</Button>}
                        {buttonsVisibility.editar_maniobra && <Button variant="contained" onClick={toggleForm} size='sm'>Editar formulario</Button>}
                        {buttonsVisibility.iniciar && <Button variant="contained" onClick={activar_maniobra} size='sm'>Iniciar</Button>}
                        {buttonsVisibility.finalizar && <Button variant="contained" onClick={finalizar_maniobra} size='sm'>Finalizar</Button>}
                        {buttonsVisibility.enviar_estatus && <Button variant="contained" onClick={registrar_maniobra} size='sm'>Enviar estatus</Button>}
                        {buttonsVisibility.reactivar && <Button variant="contained" onClick={reactivar_maniobra} size='sm'>Reactivar maniobra</Button>}
                        {buttonsVisibility.correos && <Button variant="contained" onClick={openPrimaryModal} size='sm'>Correos electronicos</Button>}
                    </div>
                </div>

                <Box sx={{ flexGrow: 1 }}>
                    <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                        <Grid lg={6} xs={12}>
                            <Card variant="outlined">
                                <CardContent>
                                    <form>
                                        <Grid container spacing={3}>
                                            <Grid item xs={12} lg={6}>
                                                <label className='mb-1'>Terminal</label>
                                                <Select
                                                    options={terminales_select}
                                                    id="terminal"
                                                    name="terminal"
                                                    value={terminales_select.find(option => option.value === formData.terminal)}
                                                    onChange={handleSelectChange}
                                                    isDisabled={formDisabled} />
                                                {errors['terminal'] && <span className='text-danger'>{errors['terminal']}</span>}
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <label className='mb-1'>Tipo de maniobra</label>
                                                <Select
                                                    id="tipo_maniobra"
                                                    name="tipo_maniobra"
                                                    onChange={handleSelectChange}
                                                    value={options_tipo_maniobra.find(option => option.value === formData.tipo_maniobra)}
                                                    options={options_tipo_maniobra}
                                                    isDisabled={formDisabled}
                                                />
                                                {errors['tipo_maniobra'] && <span className='text-danger'>{errors['tipo_maniobra']}</span>}
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <label className='mb-1'>Inicio programado:</label>
                                                <input
                                                    className='form-control'
                                                    type="datetime-local"
                                                    id="inicio_programado"
                                                    name="inicio_programado"
                                                    value={formData.inicio_programado}
                                                    onChange={handleChange}
                                                    disabled={formDisabled}
                                                />
                                                {errors['inicio_programado'] && <span className='text-danger'>{errors['inicio_programado']}</span>}
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <SelectOperador
                                                    label={'Operador'}
                                                    id={'operador_id'}
                                                    name={'operador_id'}
                                                    onChange={handleSelectChange}
                                                    value={formData.operador_id}
                                                    disabled={formDisabled}
                                                />
                                                {errors['operador_id'] && <span className='text-danger'>{errors['operador_id']}</span>}
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <MyComponent
                                                    label={'Vehiculo'}
                                                    id={'vehicle_id'}
                                                    name={'vehicle_id'}
                                                    onChange={handleSelectChange}
                                                    value={formData.vehicle_id}
                                                    tipo={'tractor'}
                                                    disabled={formDisabled}
                                                />
                                                {errors['vehicle_id'] && <span className='text-danger'>{errors['vehicle_id']}</span>}
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <MyComponent
                                                    label={'Remolque 1'}
                                                    id={'trailer1_id'}
                                                    name={'trailer1_id'}
                                                    onChange={handleSelectChange}
                                                    value={formData.trailer1_id}
                                                    tipo={'trailer'}
                                                    disabled={formDisabled}
                                                />
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <MyComponent
                                                    label={'Remolque 2'}
                                                    id={'trailer2_id'}
                                                    name={'trailer2_id'}
                                                    onChange={handleSelectChange}
                                                    value={formData.trailer2_id}
                                                    tipo={'trailer'}
                                                    disabled={formDisabled}
                                                />
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <MyComponent
                                                    label={'Dolly'}
                                                    id={'dolly_id'}
                                                    name={'dolly_id'}
                                                    onChange={handleSelectChange}
                                                    value={formData.dolly_id}
                                                    tipo={'dolly'}
                                                    disabled={formDisabled}
                                                />
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <MyComponent
                                                    label={'Motogenerador 1'}
                                                    id={'motogenerador_1'}
                                                    name={'motogenerador_1'}
                                                    onChange={handleSelectChange}
                                                    value={formData.motogenerador_1}
                                                    tipo={'other'}
                                                    disabled={formDisabled}
                                                />
                                            </Grid>
                                            <Grid item xs={12} lg={6}>
                                                <MyComponent
                                                    label={'Motogenerador 2'}
                                                    id={'motogenerador_2'}
                                                    name={'motogenerador_2'}
                                                    onChange={handleSelectChange}
                                                    value={formData.motogenerador_2}
                                                    tipo={'other'}
                                                    disabled={formDisabled}
                                                />
                                            </Grid>
                                        </Grid>
                                    </form>
                                </CardContent>
                            </Card>
                        </Grid>
                        <Grid xs={12} lg={6}>
                            {formData.estado_maniobra != null ? (
                                <Card variant="outlined">
                                    <CardContent>
                                        <div className="flex gap-4 items-center">
                                            {formData.usuario_activo !== '' && (
                                                <User
                                                    name="Iniciada por"
                                                    isBordered color="primary"
                                                    description={formData.usuario_activo}
                                                    avatarProps={{
                                                        src: ""
                                                    }}
                                                />
                                            )}
                                            {formData.usuario_finalizo !== '' && (
                                                <User
                                                    name="Finalizada por"
                                                    description={formData.usuario_finalizo}
                                                    avatarProps={{
                                                        src: ""
                                                    }}
                                                />
                                            )}
                                        </div>
                                    </CardContent>
                                    <CardContent>
                                        <div dangerouslySetInnerHTML={{ __html: htmlContent }} />
                                    </CardContent>
                                </Card>
                            ) : ''}
                        </Grid>
                        <Grid xs={12} lg={6}>
                            {id_maniobra != null ? (
                                <Card variant="outlined">
                                    <CardContent>
                                        <ManiobraContenedores
                                            id_maniobra={id_maniobra} />
                                    </CardContent>
                                </Card>) : ''}
                        </Grid>
                    </Grid>
                </Box>
            </Dialog >
        </>
    );
};

export default Formulariomaniobra;
