import { useState, useEffect } from 'react';
import MyComponent from './selects_flota';
import Select from 'react-select';
import Swal from 'sweetalert2';
import axios from 'axios';
import SelectOperador from '../maniobras/select_operador';
import ManiobraContenedores from './maniobra_contenedores';
import { Card, CardHeader, CardBody, CardFooter, Divider, Link, Image } from "@nextui-org/react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Modal from 'react-bootstrap/Modal';
import { Button, ButtonGroup } from "@nextui-org/button";
import { Avatar, AvatarGroup, AvatarIcon } from "@nextui-org/avatar";
import { User } from "@nextui-org/react";

const fieldValidations = {
    terminal: { required: true, message: 'El campo Terminal es requerido' },
    tipo_maniobra: { required: true, message: 'El campo Tipo de Maniobra es requerido' },
    operador_id: { required: true, message: 'El campo Operador es requerido' },
    vehicle_id: { required: true, message: 'El campo Vehiculo es requerido' },
    inicio_programado: { required: true, message: 'El campo Inicio Programado es requerido' }
};

const nomina_form = ({ show, handleClose, id_maniobra, id_cp }) => {
    const [formDisabled, setFormDisabled] = useState(true);
    const [htmlContent, setHtmlContent] = useState('');

    useEffect(() => {
        const controller = new AbortController();
        if (show) {
            axios
                .get("/phicargo/maniobras/maniobra_detalle/historial.php", { signal: controller.signal })
                .then((response) => {
                    setHtmlContent(response.data);
                })
                .catch((error) => {
                    if (error.name !== 'AbortError') {
                        console.error('Error al cargar el historial:', error);
                    }
                });
        }

        return () => {
            controller.abort();
        };
    }, [show]);

    const [buttonsVisibility, setButtonsVisibility] = useState({
        registrar: false,
        cancelar: false,
        editar_maniobra: false,
        iniciar: false,
        finalizar: false,
        enviar_estatus: false,
        reactivar: false
    });

    const toggleButtonsVisibility = (variable) => {
        const newVisibility = {};

        if (variable === 'borrador') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = true;
            newVisibility.cancelar = true;
            newVisibility.iniciar = true;
            newVisibility.finalizar = false;
        } else if (variable === 'cancelada') {
            newVisibility.registrar = false;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
        } else if (variable === 'registrar') {
            newVisibility.registrar = true;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
        } else if (variable === 'editar') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = false;
            newVisibility.guardar_maniobra = true;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
        } else if (variable === 'finalizada') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = false;
            newVisibility.guardar_maniobra = false;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = false;
            newVisibility.reactivar = true;
        } else if (variable === 'activa') {
            newVisibility.registrar = false;
            newVisibility.editar_maniobra = false;
            newVisibility.guardar_maniobra = false;
            newVisibility.cancelar = false;
            newVisibility.iniciar = false;
            newVisibility.finalizar = true;
            newVisibility.reactivar = false;
        }

        setButtonsVisibility(newVisibility);
    };

    const toggleForm = () => {
        setFormDisabled(false);
        toggleButtonsVisibility('editar');
    };

    const options_tipo_maniobra = [
        { value: 'ingreso', label: 'Ingreso' },
        { value: 'retiro', label: 'Retiro' }
    ];

    const [formData, setFormData] = useState({
        id_maniobra: id_maniobra,
        id_cp: id_cp,
        terminal: '',
        tipo_maniobra: '',
        operador_id: '',
        vehicle_id: '',
        trailer1_id: '',
        trailer2_id: '',
        dolly_id: '',
        inicio_programado: '',
        usuario_activo: '',
        usuario_finalizo: ''
    });

    useEffect(() => {
        if (id_maniobra) {
            setFormDisabled(true);
            axios.get(`/phicargo/maniobras/maniobra_detalle/get_maniobra.php?id_maniobra=${id_maniobra}`)
                .then((response) => {
                    const data = response.data;
                    console.log(data);
                    setFormData({
                        id_maniobra: data.id_maniobra || '',
                        id_cp: data.id_cp || '',
                        terminal: data.terminal || '',
                        tipo_maniobra: data.tipo_maniobra || '',
                        operador_id: data.operador_id || '',
                        vehicle_id: data.vehicle_id || '',
                        trailer1_id: data.trailer1_id || '',
                        trailer2_id: data.trailer2_id || '',
                        dolly_id: data.dolly_id || '',
                        motogenerador_1: data.motogenerador_1 || '',
                        motogenerador_2: data.motogenerador_2 || '',
                        inicio_programado: data.inicio_programado || '',
                        usuario_activo: data.usuarioactivacion || '',
                        usuario_finalizo: data.usuariofinalizacion || '',
                    });
                    toggleButtonsVisibility(data.estado_maniobra);
                })
                .catch((error) => {
                    console.error('Error al obtener datos de maniobra:', error);
                    Swal.fire(
                        'Error',
                        'No se pudo cargar los datos de la maniobra.',
                        'error'
                    );
                });
        } else {
            setFormDisabled(false);
            setFormData({
                id_cp: id_cp,
                terminal: '',
                tipo_maniobra: '',
                operador_id: '',
                vehicle_id: '',
                trailer1_id: '',
                trailer2_id: '',
                dolly_id: '',
                motogenerador_1: '',
                motogenerador_2: '',
                inicio_programado: '',
            });
            toggleButtonsVisibility('registrar');
        }
    }, [id_maniobra]);

    const [errors, setErrors] = useState({});
    const validateFields = () => {
        let isValid = true;
        let newErrors = {};

        Object.keys(fieldValidations).forEach(field => {
            const validation = fieldValidations[field];
            if (validation.required && !formData[field]) {
                newErrors[field] = validation.message;
                isValid = false;
            }
        });

        return { isValid, newErrors };
    };

    const validar_form = (e) => {
        e.preventDefault();
        const { isValid, newErrors } = validateFields();
        setErrors(newErrors);

        if (isValid) {
            console.log('Datos del formulario:', formData);
            registrar_maniobra();
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSelectChange = (selectedOption, { name }) => {
        setFormData((prevData) => ({
            ...prevData,
            [name]: selectedOption ? selectedOption.value : '',
        }));
        console.log('Datos del formulario:', formData);
    };

    const registrar_maniobra = () => {
        axios.post('/phicargo/maniobras/maniobra_detalle/registrar_maniobra.php', formData)
            .then((response) => {
                if (response.data === 1) {
                    Swal.fire(
                        '¡Éxito!',
                        'El registro ha sido exitoso.',
                        'success'
                    );
                    handleClose();
                } else {
                    console.error('Respuesta inesperada del servidor:', response.data);
                    Swal.fire(
                        'Error',
                        'El registro falló debido a una respuesta inesperada: ' + response.data,
                        'error'
                    );
                }
            })
            .catch((error) => {
                console.error('Error en la solicitud:', error);
                if (error.response) {
                    console.error('Respuesta de error del servidor:', error.response.data);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al registrar la maniobra. Código de error: ' + error.response.status,
                        'error'
                    );
                } else if (error.request) {
                    console.error('No se recibió respuesta del servidor:', error.request);
                    Swal.fire(
                        'Error',
                        'No se recibió respuesta del servidor.',
                        'error'
                    );
                } else {
                    console.error('Error al configurar la solicitud:', error.message);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al configurar la solicitud.',
                        'error'
                    );
                }
            });
    };

    const actualizar_maniobra = () => {
        console.log(id_maniobra);
        axios.post('/phicargo/maniobras/maniobra_detalle/actualizar_maniobra.php', formData)
            .then((response) => {
                if (response.data === 1) {
                    Swal.fire(
                        '¡Éxito!',
                        'El registro ha sido exitoso.',
                        'success'
                    );
                    handleClose();
                    setFormDisabled(true);
                    toggleButtonsVisibility('borrador');
                } else {
                    console.error('Respuesta inesperada del servidor:', response.data);
                    Swal.fire(
                        'Error',
                        'El registro falló debido a una respuesta inesperada: ' + response.data,
                        'error'
                    );
                }
            })
            .catch((error) => {
                console.error('Error en la solicitud:', error);
                if (error.response) {
                    console.error('Respuesta de error del servidor:', error.response.data);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al registrar la maniobra. Código de error: ' + error.response.status,
                        'error'
                    );
                } else if (error.request) {
                    console.error('No se recibió respuesta del servidor:', error.request);
                    Swal.fire(
                        'Error',
                        'No se recibió respuesta del servidor.',
                        'error'
                    );
                } else {
                    console.error('Error al configurar la solicitud:', error.message);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al configurar la solicitud.',
                        'error'
                    );
                }
            });
    };

    const reactivar_maniobra = () => {
        console.log(id_maniobra);
        axios.post('/phicargo/maniobras/maniobra_detalle/reactivar_maniobra.php', formData)
            .then((response) => {
                if (response.data === 1) {
                    Swal.fire(
                        '¡Éxito!',
                        'El registro ha sido exitoso.',
                        'success'
                    );
                    handleClose();
                    toggleButtonsVisibility('borrador');
                } else {
                    console.error('Respuesta inesperada del servidor:', response.data);
                    Swal.fire(
                        'Error',
                        'El registro falló debido a una respuesta inesperada: ' + response.data,
                        'error'
                    );
                }
            })
            .catch((error) => {
                console.error('Error en la solicitud:', error);
                if (error.response) {
                    console.error('Respuesta de error del servidor:', error.response.data);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al registrar la maniobra. Código de error: ' + error.response.status,
                        'error'
                    );
                } else if (error.request) {
                    console.error('No se recibió respuesta del servidor:', error.request);
                    Swal.fire(
                        'Error',
                        'No se recibió respuesta del servidor.',
                        'error'
                    );
                } else {
                    console.error('Error al configurar la solicitud:', error.message);
                    Swal.fire(
                        'Error',
                        'Hubo un problema al configurar la solicitud.',
                        'error'
                    );
                }
            });
    };

    const activar_maniobra = () => {
        Swal.fire({
            title: 'Activar',
            text: "¿Deseas realmente activar esta maniobra?",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, activar',
            cancelButtonText: 'Cancelar'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.post('/phicargo/maniobras/maniobra_detalle/activar_maniobra.php?id_maniobra=' + id_maniobra)
                    .then((response) => {
                        if (response.data === 1) {
                            Swal.fire(
                                '¡Activada!',
                                'La maniobra ha sido activada',
                                'success'
                            );
                            handleClose();
                        } else {
                            console.error(response.data);
                        }
                    })
                    .catch((error) => {
                        Swal.fire(
                            'Error',
                            'Hubo un problema al activar el registro.',
                            'error'
                        );
                    });
            }
        });
    };

    const finalizar_maniobra = () => {
        Swal.fire({
            title: 'Finalizar maniobra',
            text: "¿Realmente deseas finalizar esta maniobra?",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, finalizar',
            cancelButtonText: 'Cancelar'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.post('/phicargo/maniobras/maniobra_detalle/finalizar_maniobra.php?id_maniobra=' + id_maniobra)
                    .then((response) => {
                        if (response.data === 1) {
                            Swal.fire(
                                '¡Finalizada!',
                                'Maniobra finalizada.',
                                'success'
                            );
                            handleClose();
                        } else {
                            console.error(response.data);
                        }
                    })
                    .catch((error) => {
                        Swal.fire(
                            'Error',
                            'Hubo un problema al eliminar el registro.',
                            'error'
                        );
                    });
            }
        });
    };

    const cancelar_maniobra = () => {
        Swal.fire({
            title: '¿Estás seguro?',
            text: "¿Deseas realmente cancelar este registro?",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, cancelar',
            cancelButtonText: 'Cancelar'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.post('/phicargo/maniobras/maniobra_detalle/cancelar_maniobra.php?id_maniobra=' + id_maniobra)
                    .then((response) => {
                        if (response.data === 1) {
                            Swal.fire(
                                '¡Cancelado!',
                                'El registro ha sido cancelado.',
                                'success'
                            );
                            handleClose();
                        } else {
                            console.error(response.data);
                        }
                    })
                    .catch((error) => {
                        Swal.fire(
                            'Error',
                            'Hubo un problema al eliminar el registro.',
                            'error'
                        );
                    });
            }
        });
    };

    return (
        <>
            <Modal show={show} onHide={handleClose} fullscreen={true}>
                <Modal.Header closeButton>
                </Modal.Header>
                <Modal.Body>
                    <Row>
                        <Col lg={6}>
                            <Card>
                                <CardHeader className="flex gap-3">
                                    <h1>M-{id_maniobra}</h1>
                                    <div className="flex flex-wrap gap-2 items-center">
                                        {buttonsVisibility.registrar && <Button color="primary" onClick={validar_form} size='sm'>Registrar</Button>}
                                        {buttonsVisibility.cancelar && <Button color="danger" onClick={cancelar_maniobra} size='sm'>Cancelar</Button>}
                                        {buttonsVisibility.guardar_maniobra && <Button color="primary" size='sm' onClick={actualizar_maniobra}>Guardar cambios</Button>}
                                        {buttonsVisibility.editar_maniobra && <Button color="primary" onClick={toggleForm} size='sm'>Editar formulario</Button>}
                                        {buttonsVisibility.iniciar && <Button color="success" onClick={activar_maniobra} size='sm'>Iniciar</Button>}
                                        {buttonsVisibility.finalizar && <Button color="primary" onClick={finalizar_maniobra} size='sm'>Finalizar</Button>}
                                        {buttonsVisibility.enviar_estatus && <Button color="primary" onClick={registrar_maniobra} size='sm'>Enviar estatus</Button>}
                                        {buttonsVisibility.reactivar && <Button color="warning" onClick={reactivar_maniobra} size='sm'>Reactivar maniobra</Button>}
                                    </div>
                                </CardHeader>
                                <Divider />
                                <CardBody>
                                    <form>
                                        <Row>
                                            <Col>
                                                <div className='mb-3'>
                                                    <label className='mb-1'>Terminal</label>
                                                    <input
                                                        className='form-control'
                                                        type="text"
                                                        id="terminal"
                                                        name="terminal"
                                                        value={formData.terminal}
                                                        onChange={handleChange}
                                                        disabled={formDisabled}
                                                    />
                                                    {errors['terminal'] && <span className='text-danger'>{errors['terminal']}</span>}
                                                </div></Col>
                                            <Col>
                                                <div className="mb-3">
                                                    <label className='mb-1'>Tipo de maniobra</label>
                                                    <Select
                                                        id="tipo_maniobra"
                                                        name="tipo_maniobra"
                                                        onChange={handleSelectChange}
                                                        value={options_tipo_maniobra.find(option => option.value === formData.tipo_maniobra)}
                                                        options={options_tipo_maniobra}
                                                        isDisabled={formDisabled}
                                                    />
                                                    {errors['tipo_maniobra'] && <span className='text-danger'>{errors['tipo_maniobra']}</span>}
                                                </div>
                                            </Col>
                                            <Col>
                                                <div className='mb-3'>
                                                    <label className='mb-1'>Inicio programado:</label>
                                                    <input
                                                        className='form-control'
                                                        type="datetime-local"
                                                        id="inicio_programado"
                                                        name="inicio_programado"
                                                        value={formData.inicio_programado}
                                                        onChange={handleChange}
                                                        disabled={formDisabled}
                                                    />
                                                    {errors['inicio_programado'] && <span className='text-danger'>{errors['inicio_programado']}</span>}
                                                </div>
                                            </Col>
                                        </Row>
                                        <div className='mb-3'>
                                            <SelectOperador
                                                label={'Operador'}
                                                id={'operador_id'}
                                                name={'operador_id'}
                                                onChange={handleSelectChange}
                                                value={formData.operador_id}
                                                disabled={formDisabled}
                                            />
                                            {errors['operador_id'] && <span className='text-danger'>{errors['operador_id']}</span>}
                                        </div>
                                    </form>
                                </CardBody>
                            </Card>
                            <Card className="mt-2">
                                <CardBody>
                                    {id_maniobra != null ? (
                                        <ManiobraContenedores
                                            id_maniobra={id_maniobra} />)
                                        : ''}
                                </CardBody>
                            </Card>
                        </Col>
                        <Col lg={6}>
                            <Card>
                                <CardHeader>
                                    <div className="flex gap-4 items-center">
                                        {formData.usuario_activo !== '' && (
                                            <User
                                                name="Iniciada por"
                                                isBordered color="primary"
                                                description={formData.usuario_activo}
                                                avatarProps={{
                                                    src: ""
                                                }}
                                            />
                                        )}
                                        {formData.usuario_finalizo !== '' && (
                                            <User
                                                name="Finalizada por"
                                                description={formData.usuario_finalizo}
                                                avatarProps={{
                                                    src: ""
                                                }}
                                            />
                                        )}
                                    </div>
                                </CardHeader>
                                <Divider />
                                <CardBody>
                                    <Container>
                                        <Row>
                                            <Col>
                                                <div dangerouslySetInnerHTML={{ __html: htmlContent }} />
                                            </Col>
                                        </Row>
                                    </Container>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default Nomina_form;
